import 'package:app/frontPageLogin.dart';

class TwitterHome extends StatefulWidget {
  const TwitterHome({Key? key}) : super(key: key);

  @override
  _TwitterHomeState createState() => _TwitterHomeState();
}

class _TwitterHomeState extends State<TwitterHome> {
  bool twitteando = false;
  int actualelegido = 0;
  List<Tweet> _tweets = [
    Tweet(
      name: "Jack",
      username: "@jackjhonson",
      content: "Go check out my new single on Spotify: Go on.",
      isLiked: false,
      likesCount: 12652,
      profileImageUrl:
          "data:image/png;base64,",
    ),
    Tweet(
      name: "Paul",
      username: "@paulD1",
      content: "Help i cant stop cancelling meetings",
      isLiked: false,
      likesCount: 52,
      profileImageUrl:
          "data:image/png;base64,",
    ),
    Tweet(
      name: "Twitter",
      username: "@twitter",
      content: "Memes have an expiration date",
      isLiked: false,
      likesCount: 269012,
      profileImageUrl:
          "data:image/png;base64,",
    ),
    Tweet(
      name: "Kari",
      username: "@karilopez",
      content: "I need 3 field Red Hot Chilli Peeppers tickets!!!",
      isLiked: false,
      likesCount: 3,
      profileImageUrl:
          "data:image/png;base64,",
    ),
    Tweet(
      name: "LALakers",
      username: "@lakersfan1",
      content: "Lets get to the nba finals!!! We are one step away.",
      isLiked: false,
      likesCount: 12652,
      profileImageUrl:
          "data:image/png;base64,",
    ),
    Tweet(
      name: "Vera",
      username: "@veracord20",
      content: "There are too many people outside",
      isLiked: false,
      likesCount: 34,
      profileImageUrl:
          "data:image/png;base64,",
    ),
    Tweet(
      name: "Anna",
      username: "@Annakk9",
      content: "I love this cold",
      isLiked: false,
      likesCount: 269012,
      profileImageUrl:
          "data:image/png;base64,",
    ),
    Tweet(
      name: "Mateo",
      username: "@MateoDall",
      content: "I need 6 field Red Hot Chilli Peeppers tickets!!!",
      isLiked: false,
      likesCount: 88,
      profileImageUrl:
          "data:image/png;base64,",
    ),
  ];

  void changeLike(int index) {
    // solo le paso a que tweet
    setState(() {
      _tweets[index].isLiked =
          !_tweets[index].isLiked; //si esta true lño seteo false y al reves
      if (_tweets[index].isLiked) {
        _tweets[index].likesCount++;
      } else {
        _tweets[index].likesCount--;
      }
    });
  }

  String likesEnTweet(int cantLikes) {
    if (cantLikes > 9999) {
      double likesInK = cantLikes / 1000;
      String likesstring = likesInK.toStringAsFixed(1);
      return likesstring + 'k';
    } else {
      return cantLikes.toString();
    }
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        automaticallyImplyLeading: false,
        backgroundColor: Color.fromARGB(255, 255, 255, 255),
        title: Row(
