            ),
            subtitle: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  _tweets[index].content,
                  style: TextStyle(
                    color: Color.fromARGB(255, 87, 87, 87),
                  ),
                ),
                Row(
                  children: [
                    SizedBox(width: 4),
                    GestureDetector(
                      onTap: () => changeLike(index),
                      child: Icon(
                        _tweets[index].isLiked
                            ? Icons.favorite
                            : Icons.favorite_border,
                        color: _tweets[index].isLiked ? Colors.red : null,
                      ),
                    ),
                    SizedBox(width: 5),
                    Text(
                      likesEnTweet(_tweets[index].likesCount),
                    ),
                  ],
                ),
                SizedBox(
                  width: double.infinity,
                  child: Divider(
                    color: Colors.black,
                    thickness: 0.2,
                  ),
                ),
              ],
            ),
          );
        },
      ),
      bottomNavigationBar: BottomNavigationBar(
        currentIndex: actualelegido,
        onTap: (int index) {
          setState(() {
            actualelegido = index;
          });
        },
        unselectedItemColor: const Color.fromARGB(255, 99, 98, 98),
        selectedItemColor: Color.fromARGB(255, 85, 153, 205),
        items: const <BottomNavigationBarItem>[
